import React, { useState } from 'react';
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
// import TextField from "@mui/material/TextField";
// import FormControlLabel from "@mui/material/FormControlLabel";
// import Checkbox from "@mui/material/Checkbox";
// import Link from "@mui/material/Link";
// import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Form from "react-bootstrap/Form";

import Swal from 'sweetalert2';
import '../css/SignUp.css';
import axios from "axios";

    const URL = 'http://localhost:3001/register';

    const theme = createTheme();
    
        const [inputs, setInputs] = useState({
            // id: "",
            fistName: "",
            lastName: "",
            email: "",
            password: ""
        });

        const handleChange = (e) => {
            setInputs({
            ...inputs,
            [e.target.name]: e.target.value,
            });
        };

        const handleClick = async () => {
            await axios.post(URL, inputs)
            setInputs({
            // id: "",
            fistName: "",
            lastName: "",
            email: "",
            password: ""
            })

        Swal.fire({
            title: 'Data sent',
            text: 'Thanks to sent your data',
            icon: 'success',
            confirmButtonText: 'Accept'
        })

    return (
        <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs" className="box">
            <CssBaseline />
            <Box
            sx={{
                marginTop: 8,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}
            >
            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}></Avatar>
            <Typography component="h1" variant="h5">
                    Sign up
                </Typography>
                <Form className="container mt-5">
                <h1>Sign Up</h1>
                <div className="d-flex flex-column align-items-center">
                {Object.keys(inputs).map((key, index) => (
                    <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                    <Form.Label id="form">
                        {key === "" ? key.toUpperCase() : key[0].toUpperCase() + key.slice(1)}</Form.Label>
                    <Form.Control
                        name={key}
                        value={inputs[key]}
                        onChange={handleChange}
                    />
                    </Form.Group>
                ))}
                    <Button className="mt-3" onClick={handleClick}>Submit</Button>
                </div>
            </Form>
            </Box>
        </Container>
        </ThemeProvider>
    );
}
