import React, { useState } from 'react';
import Form from "react-bootstrap/Form";
import axios from "axios";
import "../css/SignUp.css";
import Swal from 'sweetalert2';

// import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
// import CssBaseline from "@mui/material/CssBaseline";
// import TextField from "@mui/material/TextField";
// import Grid from "@mui/material/Grid";
// import Box from "@mui/material/Box";
// import Typography from "@mui/material/Typography";
// import Container from "@mui/material/Container";
// import { createTheme, ThemeProvider } from "@mui/material/styles";

const SignUp = () => {
    
        const URL = 'http://localhost:3001/register';

        const [inputs, setInputs] = useState({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            password:""
        });

        const handleChange = (e) => {
            setInputs({
            ...inputs,
            [e.target.name]: e.target.value,
            });
        };

        const handleClick = async () => {
            await axios.post(URL, inputs)
            setInputs({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            password:""
            })

        Swal.fire({
            title: 'Data sent',
            text: 'Thanks to sent your data',
            icon: 'success',
            confirmButtonText: 'Accept'
        })

    };

    // const theme = createTheme();
    
    // const handleSubmit = (event) => {
    //     event.preventDefault();
    //     const data = new FormData(event.currentTarget);
    //     console.log({
    //     email: data.get("email"),
    //     password: data.get("password"),
    //     });
    // };

    return (
        <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs" className="box">
            <CssBaseline />
            <Box
            sx={{
                marginTop: 8,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}
            >
            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}></Avatar>
            <Typography component="h1" variant="h5">
                Sign up
            </Typography>
            <Box
                component="form"
                noValidate
                onSubmit={handleSubmit}
                sx={{ mt: 3 }}
            >
            <Grid container spacing={2}>              
                <Form className="container mt-5">
                    <div className="d-flex flex-column align-items-center">
                    {Object.keys(inputs).map((key, index) => (
                        <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                        <Form.Label id="form">
                            {key === "" ? key.toUpperCase() : key[0].toUpperCase() + key.slice(1)}</Form.Label>
                        <Form.Control
                            name={key}
                            value={inputs[key]}
                            onChange={handleChange}
                        />
                        </Form.Group>
                    ))}
               
                </div>
                </Form>  </Grid>       
                </Box>            
                </Box>
                <Button className="mt-3" onClick={handleClick}>Submit</Button>
        </Container>
        </ThemeProvider>

    );
}

export default SignUp;
