      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        axios.delete(`${URL}${id}`);
        Swal.fire("Deleted", "", "success");
        getContacts();
      } else if (result.isDenied) {
        Swal.fire("This message has not been deleted");
      }
    });
  };


  return (
