import { useState , useContext } from 'react';
import { JobContext } from '../context/JobContext'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form'


function JobForm() {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const { createJob } = useContext(JobContext)

    const handleSubmit = (e) => {
        e.preventDefault();
        createJob({
            title,
            description
        });
        setTitle("");
        setDescription("");
    };

    return (
            <Form className="container mt-5">
                <br/>
                <br/>
                <br/>
                <br/>
                <Form.Group className="mb-3" controlId="formBasicEmail" onSubmit={handleSubmit}>
                    <Form.Label>Job Title</Form.Label>
                    <Form.Control type="title" placeholder="Enter job title"
                    onChange={(e) => setTitle(e.target.value)}
                    value={title}
                    autoFocus />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Job Description</Form.Label>
                    <Form.Control 
                    type="description" placeholder="Enter job description" 
                    onChange={(e) => setDescription(e.target.value)}
                    value={description}
                    />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
                </Form>
    );
}

export default JobForm;
