import React, { useState } from 'react';
import Form from "react-bootstrap/Form";
import axios from "axios";
import "../css/SignUp.css";
import Swal from 'sweetalert2';
v
import '../css/SignUp.css'

const SignUp = () => {
    
const URL = 'http://localhost:3001/register';

    const [inputs, setInputs] = useState({
        // id: "",
        firstName: "",
        lastName: "",
        email: "",
        password:""
    });

    const handleChange = (e) => {
        setInputs({
        ...inputs,
        [e.target.name]: e.target.value,
            });
        };

        const handleClick = async () => {
            await axios.post(URL, inputs)
            setInputs({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            password:""
            })

        Swal.fire({
            title: 'Data sent',
            text: 'Thanks to sent your data',
            icon: 'success',
            confirmButtonText: 'Accept'
        })

    };
    
    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
        firstName: data.get("firstName"),
        lastName: data.get("lastName"),            
        email: data.get("email"),
        password: data.get("password"),
        });
    };

    return (
        <div>
        <Form className="container mt-5">
            <div className="d-flex flex-column align-items-center">
            {Object.keys(inputs).map((key, index) => (
                <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                <Form.Label id="form">
                    {key === "" ? key.toUpperCase() : key[0].toUpperCase() + key.slice(1)}</Form.Label>
                <Form.Control
                    name={key}
                    value={inputs[key]}
                    onChange={handleChange}
                />
                </Form.Group>
            ))}
                <Button className="mt-3" onClick={handleClick}>Submit</Button>
            </div>
        </Form>
</div>
);
}

export default SignUp;
