import { Table, Button } from "react-bootstrap";
import axios from "axios";
import React, { useState, useEffect } from "react";
import Swal from "sweetalert2";

const Messages = () => {
  const [messages, setContact] = useState([]);

  const URL = "http://localhost:3001/contact";

  const getContact = async () => {
    try {
      const { data } = await axios.get(URL);
      setContact(data.contact);
      console.log(data.contact);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getContact()
  }, [])


  const handleDelete = (id) => {
    console.log(id);

    Swal.fire({
      title: "Are you sure you want to delete this user?",
      showDenyButton: true,
      confirmButtonText: "Delete",
      denyButtonText: `Exit`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        axios.delete(`${URL}${id}`);
        Swal.fire("Deleted", "", "success");
        getContact();
      } else if (result.isDenied) {
        Swal.fire("This message has not been deleted");
      }
    });
  };


  return (
    <div className="container">
      <h2 className="m-4">Users</h2>
      <Table className="container m-2 table striped bordered hover">
        <thead className="container text-left m-5">
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th className="text-center">Actions</th>
          </tr>
        </thead>
        {messages.map((user) => (
          <tbody>
            <tr className="body">
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.email}</td>
              <td className="text-center">
                <Button
                  variant="danger"
                  onClick={() => handleDelete(user._id)}
                  className="m-1"
                >
                  Delete
                </Button>
              </td>
            </tr>
          </tbody>
        ))}
      </Table>
    </div>
  );
};

export default Messages;
