    import React, { useState } from "react";
    import axios from "axios";
    import "../css/SignUp.css";
    import Swal from "sweetalert2";
    import { Form, Button } from "react-bootstrap";

    import Link from "@mui/material/Link";
    import TextField from "@mui/material/TextField";
    import Avatar from "@mui/material/Avatar";
    import CssBaseline from "@mui/material/CssBaseline";
    import Box from "@mui/material/Box";
    import FormControlLabel from "@mui/material/FormControlLabel";
    import Typography from "@mui/material/Typography";
    import { createTheme, ThemeProvider } from "@mui/material/styles";
    import Grid from "@mui/material/Grid";

function SignUp() {

    const URL = "http://localhost:3001/register";

    const theme = createTheme();

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
        firstName: data.get("firstName"),
        lastName: data.get("lastName"),
        email: data.get("email"),
        password: data.get("password"),
        });
    };

    const [inputs, setInputs] = useState({
        // id: "",
        firstName: "",
        lastName: "",
        email: "",
        password: "",
    });

    const handleChange = (e) => {
        setInputs({
        ...inputs,
        [e.target.name]: e.target.value,
        });
    };

    const handleClick = async () => {
        await axios.post(URL, inputs);
        setInputs({
        // id: "",
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        });

        Swal.fire({
        title: "Data sent",
        text: "Thanks to sent your data",
        icon: "success",
        confirmButtonText: "Accept",
        });
    };

    return (

    <ThemeProvider theme={theme}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: "url(https://www.uplers.com/wp-content/uploads/2020/06/Exclusive-Research-on-Why-Remote-Working-is-the-Future-of-IT-Industry-1.jpg)",
            backgroundRepeat: "no-repeat",
            backgroundColor: (t) =>
              t.palette.mode === "light"
                ? t.palette.grey[50]
                : t.palette.grey[900],
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        />
            <Box
            sx={{
                marginTop: 8,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}
            >
            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}></Avatar>
            <Typography component="h1" variant="h5">
                Sign up
            </Typography>
            <Box
              component="form"
              noValidate
              onSubmit={handleSubmit}
              sx={{ mt: 1 }}
            >
            <div className="d-flex flex-column align-items-center">
            {Object.keys(inputs).map((key, index) => (
                <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                <TextField
                    margin="normal"
                    fullWidth
                    name={key}
                    label={key}
                    id="form"
                    > 
                    <FormControlLabel
                    name={key}
                    value={inputs[key]}
                    onChange={handleChange}
                    />
                </TextField>
                </Form.Group>
            ))}
                <Button 
                className="mt-3" 
                onClick={handleClick}
                type="submit"
                fullWidth
                id="btn"
                variant="warning"
                sx={{ mt: 3, mb: 2 }}
                >
                Submit
                </Button>
                <Grid item>
                  <Link href="/usertype" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>

            </div>
        </Box>
        </Box>
        </ThemeProvider>
    );
    }
export default SignUp;
