// import Button from "react-bootstrap/Button";
// import Card from "react-bootstrap/Card";
// import Nav from "react-bootstrap/Nav";
import "../css/JobCard.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
// import Swal from 'sweetalert2';


function JobCard() {

    const [jobTitle, setJobTitle] = useState("");
    const [jobDescription, setJobeDescription] = useState("");

  
    const jobData = async () => {
      try {
        const res = await axios.get("https://localhost:3001/");
        setJobTitle(res.data.results[0].title);
      } catch (error) {
        console.log(error);
      }
    };
  
    useEffect(() => {
      jobData();
    }, []);
  
    return (
      <div>
        <button onClick={() => jobData()}>New Person</button>
        <div className="card">
          <h1>{jobTitle}</h1>
          <p className="title">{jobEmail}</p>
          <p>{jobDescription}</p>
          <p>
            <button>Contact</button>
          </p>
        </div>
      </div>
    );
}

export default JobCard;
