import React, { useState } from 'react';
import Form from "react-bootstrap/Form";
import axios from "axios";
import "../css/SignUp.css";
import Swal from 'sweetalert2';

import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
// import TextField from "@mui/material/TextField";
// import FormControlLabel from "@mui/material/FormControlLabel";
// import Checkbox from "@mui/material/Checkbox";
import Link from "@mui/material/Link";
// import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import { createTheme, ThemeProvider } from "@mui/material/styles";

import '../css/SignUp.css'

function Copyright(props) {
    return (
      <Typography
        variant="body2"
        color="text.secondary"
        align="center"
        {...props}
      >
        {"Copyright © "}
        <Link color="inherit" href="/">
          Remotely
        </Link>{"/"}
        {new Date().getFullYear()}
        {"."}
      </Typography>
    );
  }  
  
  const theme = createTheme();

const SignUp = () => {
    
        const URL = 'http://localhost:3001/register';

        const [inputs, setInputs] = useState({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            password:""
        });

        const handleChange = (e) => {
            setInputs({
            ...inputs,
            [e.target.name]: e.target.value,
            });
        };

        const handleClick = async () => {
            await axios.post(URL, inputs)
            setInputs({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            password:""
            })

        Swal.fire({
            title: 'Data sent',
            text: 'Thanks to sent your data',
            icon: 'success',
            confirmButtonText: 'Accept'
        })

    };
    
    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
        firstName: data.get("firstName"),
        lastName: data.get("lastName"),            
        email: data.get("email"),
        password: data.get("password"),
        });
    };

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
                <Grid
                    item
                    xs={false}
                    sm={4}
                    md={7}
                    sx={{
                        backgroundImage: "url(https://www.uplers.com/wp-content/uploads/2020/06/Exclusive-Research-on-Why-Remote-Working-is-the-Future-of-IT-Industry-1.jpg)",
                        backgroundRepeat: "no-repeat",
                        backgroundColor: (t) =>
                        t.palette.mode === "light"
                            ? t.palette.grey[50]
                            : t.palette.grey[900],
                        backgroundSize: "cover",
                        backgroundPosition: "center",
                    }}
                />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
                    my: 8,
                    mx: 4,
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
      
            }}
            >
            
        <Avatar sx={{ m: 1, bgcolor: "warning.dark" }}> </Avatar>
        <Typography component="h1" variant="h5">
            Sign Up
        </Typography>
            {/* <Box
                component="form"
                noValidate
                onSubmit={handleSubmit}
                sx={{ mt: 1 }}
            >
   
            <Grid spacing={2}>              
                <Form className="container mt-5">
                    <div className="d-flex flex-column align-items-center">
                    {Object.keys(inputs).map((key, index) => (
                        <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                        <Form.Label id="form">
                            {key === "" ? key.toUpperCase() : key[0].toUpperCase() + key.slice(1)}</Form.Label>
                        <Form.Control
                            name={key}
                            value={inputs[key]}
                            onChange={handleChange}
                        />
                        </Form.Group>
                    ))}
                </div>
                </Form>  
                </Grid>       
                </Box>             */}
                </Box>
                </Grid>
                <Button className="mt-3" onClick={handleClick}>Submit</Button>
        </ThemeProvider>

    );
}

export default SignUp;
