        setJobDescription(res.data.results[0].descrption);
        setJobs(data.job)
        console.log(data.job);
        } catch (error) {
        console.error(error);
        }
    }


    useEffect( () => {
    axios.get('http://localhost:3001/').then((response) => {
        getJobs();
        // setJobs(response.data.job);
        // console.log(response.data.job);
    });
}, [])


    const handleDelete = (id) => {
        console.log(id);
    
        Swal.fire({
            title: 'Are you sure you want to delete this job?',
            showDenyButton: true,
            confirmButtonText: 'Delete',
            denyButtonText: `Exit`,
            }).then((result) => {
            /* Read more about isConfirmed, isDenied below */
            if (result.isConfirmed) {
            axios.delete(`${URL}${id}`)
            Swal.fire('Deleted', '', 'success')
            getJobs()
            } else if (result.isDenied) {
            Swal.fire('This job has not been deleted')
            }
        })}

return (
        <Card className="m-2 py-1 card shadow-sm">
        <Card.Header>
            <Nav variant="pills" defaultActiveKey="#first">
            <Nav.Item>
                <Nav.Link href="#first">About</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#link">Job Poster</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#disabled" disabled>
                Easy Apply
                </Nav.Link>
            </Nav.Item>
            </Nav>
        </Card.Header>
        {jobs.map((job) => (
            <Card.Body>
            <Card.Title>{job.title}</Card.Title>
            <Card.Text>{job.description}</Card.Text>
            <Button>Apply</Button>
            <Button variant="danger m-1" onClick={() => handleDelete(job._id)}className="mt-3">Delete</Button>
            </Card.Body>
        ))}
        </Card>
    );
}

export default JobCard;
