import '../css/Navbar.css';
import styled from 'styled-components'
import BurguerButton from './BurguerButton'

export default function Navbar() {

  const [clicked, setClicked] = useState(false)
  const handleClick = () => {
    setClicked(!clicked)

  return (
    <NavContainer>
        <span>Responsive</span>
    <header className="p-3 text-bg-dark">
    <div className="container-fluid">
      <div className="d-flex flex-wrap align-items-center justify-content-center justify-content-lg-start">
      <h1 className="logo">
              <a href="/">
                <img src="https://i.postimg.cc/1Xg9C6hQ/LOGO-REMOTELY-copia.png" alt="" className="img-fluid" />
              </a>
              </h1>

        <ul className="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0" id="text">
          <li><a href="/" className="nav-link px-4 text-secondary">Home</a></li>
          <li><a href="/jobs" className="nav-link px-4 text-white">Jobs</a></li>
          <li><a href="/form" className="nav-link px-4 text-white">Upload Jobs</a></li>
          <li><a href="/about" className="nav-link px-4 text-white">About</a></li>
          <li><a href="/contact" className="nav-link px-4 text-white">Contact</a></li>
        </ul>

        {/* <form className="col-12 col-lg-auto mb-3 mb-lg-0 me-lg-5" role="search">
          <input type="search" className="form-control form-control-dark text-bg-dark" placeholder="Search..." aria-label="Search"/>
        </form> */}

        <div className="text-end">
          <a href="/signup">
          <button type="button" id="btn" className="btn btn-outline-dark" >Sign-up</button>
          </a>
          </div>

      </div>
    </div>
  </header>
  <div className='burguer'>
          <BurguerButton clicked={clicked} handleClick={handleClick} />
        </div>
        <BgDiv className={`initial ${clicked ? ' active' : ''}`}></BgDiv>
    </NavContainer>
  );
}
}

const NavContainer = styled.nav`
  h2{
    color: white;
    font-weight: 400;
    span{
      font-weight: bold;
    }
  }
  padding: .4rem;
  background-color: #333;
  display: flex;
  align-items: center;
  justify-content: space-between;
  a{
    color: white;
    text-decoration: none;
    margin-right: 1rem;
  }
  .links{
    position: absolute;
    top: -700px;
    left: -2000px;
    right: 0;
    margin-left: auto;
    margin-right: auto;
    text-align: center;
    transition: all .5s ease;
    a{
      color: white;
      font-size: 2rem;
      display: block;
    }
    @media(min-width: 768px){
      position: initial;
      margin: 0;
      a{
        font-size: 1rem;
        color: white;
        display: inline;
      }
      display: block;
    }
  }
  .links.active{
    width: 100%;
    display: block;
    position: absolute;
    margin-left: auto;
    margin-right: auto;
    top: 30%;
    left: 0;
    right: 0;
    text-align: center;
    a{
      font-size: 2rem;
      margin-top: 1rem;
      color: white;
    }
  }
  .burguer{
    @media(min-width: 768px){
      display: none;
    }
  }
`

const BgDiv = styled.div`
  background-color: #222;
  position: absolute;
  top: -1000px;
  left: -1000px;
  width: 100%;
  height: 100%;
  z-index: -1;
  transition: all .6s ease ;
  
  &.active{
    border-radius: 0 0 80% 0;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  `
