import { useContext } from 'react';
import { JobContext } from "../context/JobContext";

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Nav from 'react-bootstrap/Nav';
import './';

function JobCard({ job }) {

    const { deleteJob } = useContext(JobContext);

    return(
        <Card className="m-2 py-1 card shadow-sm">
        <Card.Header>
        <Nav variant="pills" defaultActiveKey="#first">
            <Nav.Item>
            <Nav.Link href="#first">About</Nav.Link>
            </Nav.Item>
            <Nav.Item>
            <Nav.Link href="#link">Job Poster</Nav.Link>
            </Nav.Item>
            <Nav.Item>
            <Nav.Link href="#disabled" disabled>
                Easy Apply
            </Nav.Link>
            </Nav.Item>
        </Nav>
        </Card.Header>
        <Card.Body>
        <Card.Title>{job.title}</Card.Title>
        <Card.Text>
        {job.description}
        </Card.Text>
        <Button>Apply</Button>
                <Button variant="danger m-1" onClick={() => deleteJob(job.id)}>Delete</Button>
        </Card.Body>
        </Card>
        
    );
}

export default JobCard;
