import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";
import "../css/JobForm.css";
import Swal from "sweetalert2";

const JobForm = () => {
  const [jobs, setJobs] = useState([]);

  const URL = "http://localhost:3001/upload";

  const getJobs = async () => {
    try {
      const { data } = await axios.get(URL);
      setJobs(data.job);
      console.log(data.job);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getPersonas();
  }, []);

  const handleDelete = (id) => {
    console.log(id);

    Swal.fire({
      title: "Está seguro de ELIMINAR?",
      showDenyButton: true,
      confirmButtonText: "Eliminar",
      denyButtonText: `No Eliminar`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        axios.delete(`${URL}${id}`);
        Swal.fire("Eliminado", "", "success");
        getPersonas();
      } else if (result.isDenied) {
        Swal.fire("Sus datos no se eliminaron");
      }
    });
  };

  return (
    <div>
      <div className="container-fluid">
        <div className="container px-6 py-5" id="custom-cards">
          <h2 className="pb-2 border-bottom">Why us</h2>
          <div className="row row-cols-1 row-cols-lg-3 align-items-stretch g-4 py-5">
            <div className="col">
              <div
                id="card1"
                className="card card-cover h-100 overflow-hidden text-white bg-dark rounded-5 shadow-lg"
              >
                <div className="d-flex flex-column h-100 p-5 pb-3 text-white text-shadow-1">
                  <h2 className="pt-5 mt-5 mb-4 display-6 lh-1 fw-bold">
                    Proof of quality
                  </h2>
                  <ul className="d-flex list-unstyled mt-auto">
                    <li className="me-auto">
                      <img
                        src="https://i.postimg.cc/9Mk72SJn/Dise-o-sin-t-tulo-9.png"
                        alt="Quality"
                        width="32"
                        height="32"
                        className="rounded-circle border border-white"
                      />
                    </li>
                  </ul>
                </div>
              </div>
            </div>

            <div className="col">
              <div
                id="card2"
                className="card card-cover h-100 overflow-hidden text-white bg-dark rounded-5 shadow-lg"
              >
                <div className="d-flex flex-column h-100 p-5 pb-3 text-white text-shadow-1">
                  <h2 className="pt-5 mt-5 mb-4 display-6 lh-1 fw-bold">
                    No cost until you hire
                  </h2>
                  <ul className="d-flex list-unstyled mt-auto">
                    <li className="me-auto">
                      <img
                        src="https://i.postimg.cc/FKspsMVr/Dise-o-sin-t-tulo-10.png"
                        alt="Cost"
                        width="32"
                        height="32"
                        className="rounded-circle border border-white"
                      />
                    </li>
                  </ul>
                </div>
              </div>
            </div>

            <div className="col">
              <div
                id="card3"
                className="card card-cover h-100 overflow-hidden text-white bg-dark rounded-5 shadow-lg"
              >
                <div className="d-flex flex-column h-100 p-5 pb-3 text-shadow-1">
                  <h2 className="pt-5 mt-5 mb-4 display-6 lh-1 fw-bold">
                    Safe and secure
                  </h2>
                  <ul className="d-flex list-unstyled mt-auto">
                    <li className="me-auto">
                      <img
                        src="data:image/png;base64,"
                        alt="Security"
                        width="32"
                        height="32"
                        className="rounded-circle border border-white"
                      />
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>

        <Form className="container mt-5">
          <h1>Upload a job</h1>
          <div className="d-flex flex-column align-items-center">
            {Object.keys(inputs).map((key, index) => (
              <Form.Group
                className="pt-3"
                key={index}
                style={{ maxWidth: "200px" }}
              >
                <Form.Label id="form">
                  {key === "budget"
                    ? key.toUpperCase()
                    : key[0].toUpperCase() + key.slice(1)}
                </Form.Label>
                <Form.Control
                  name={key}
                  value={inputs[key]}
                  onChange={handleChange}
                />
              </Form.Group>
            ))}
            <Button className="mt-3" onClick={handleClick}>
              Enviar
            </Button>
          </div>
        </Form>
      </div>
    </div>
  );
};

export default JobForm;
