    import React, { useState } from "react";
    import axios from "axios";
    import "../css/SignUp.css";
    import Swal from "sweetalert2";
    import { Form, Button } from "react-bootstrap";


    import Avatar from "@mui/material/Avatar";
    import CssBaseline from "@mui/material/CssBaseline";
    import Box from "@mui/material/Box";

    import Typography from "@mui/material/Typography";
    import { createTheme, ThemeProvider } from "@mui/material/styles";
    
    const theme = createTheme();

    function Copyright(props) {
        return (
          <Typography
            variant="body2"
            color="text.secondary"
            align="center"
            {...props}
          >
            {"Copyright © "}
            <Link color="inherit" href="/">
              Remotely
            </Link>{"/"}
            {new Date().getFullYear()}
            {"."}
          </Typography>
        );
      }

 function SignUp() {

    const theme = createTheme();

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
        firstName: data.get("firstName"),
        lastName: data.get("lastName"),
        email: data.get("email"),
        password: data.get("password"),
        });
    };

    const URL = "http://localhost:3001/register";

    const [inputs, setInputs] = useState({
        // id: "",
        firstName: "",
        lastName: "",
        email: "",
        password: "",
    });

    const handleChange = (e) => {
        setInputs({
        ...inputs,
        [e.target.name]: e.target.value,
        });
    };

    const handleClick = async () => {
        await axios.post(URL, inputs);
        setInputs({
        // id: "",
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        });

        Swal.fire({
        title: "Data sent",
        text: "Thanks to sent your data",
        icon: "success",
        confirmButtonText: "Accept",
        });
    };

    return (

    <ThemeProvider theme={theme}>
        <CssBaseline />
            <Box
            sx={{
                marginTop: 8,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}
            >
            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}></Avatar>
            <Typography component="h1" variant="h5">
                Sign up
            </Typography>

        <Form className="container mt-5">
            <div className="d-flex flex-column align-items-center">
            {Object.keys(inputs).map((key, index) => (
                <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                <Form.Label id="form">
                    {key === "" ? key.toUpperCase() : key[0].toUpperCase() + key.slice(1)}</Form.Label>
                <Form.Control
                    name={key}
                    value={inputs[key]}
                    onChange={handleChange}
                />
                </Form.Group>
            ))}
                <Button className="mt-3" onClick={handleClick}>Submit</Button>
            </div>
        </Form>
        </Box>
        </ThemeProvider>
    );
    }
export default SignUp;
