import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Nav from "react-bootstrap/Nav";
import "../css/JobCard.css";
// import axios from "axios";
// import React, { useState, useEffect } from "react";

function JobCard() {

return (
        <Card className="m-2 py-1 card shadow-sm">
        <Card.Header>
            <Nav variant="pills" defaultActiveKey="#first">
            <Nav.Item>
                <Nav.Link href="#first">About</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#link">Job Poster</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#disabled" disabled>
                Easy Apply
                </Nav.Link>
            </Nav.Item>
            </Nav>
        </Card.Header>
        {jobs.map((job) => (
            <Card.Body>
            <Card.Title>{job.title}</Card.Title>
            <Card.Text>{job.description}</Card.Text>
            <Button>Apply</Button>
            <Button variant="danger m-1">Delete</Button>
            </Card.Body>
        ))}
        ,
        </Card>
    );
}
 <tbody className="m-4">
                    {jobs.map(job => 
                        <tr>
                        {/* <td>{job._id}</td> */}
                        <td>{job.title}</td>
                        <td>{job.description}</td>
                        <td>{job.budget}</td>
                        <td><Button variant="success" className="mt-3">Update</Button></td>
                        <td><Button variant="danger" onClick={() => handleDelete(job._id)}className="mt-3">Delete</Button></td>
                        </tr>
                    )}
                    </tbody>
export default JobCard;
