import { useEffect, useState } from 'react';
import '../css/JobList.css';
// import JobCard from './JobCard';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';
import Swal from 'sweetalert2';


function JobList() {
        
        const [jobs, setJobs] = useState([]);

        const URL = 'http://localhost:3001/';

        // const getJobs = async () => {
        //     try {
        //     const { data } = await axios.get(URL);
        //     setJobs(data.job)
        //     console.log(data.job);
        //     } catch (error) {
        //     console.error(error);
        //     }
        // }
        
        useEffect(() => {
            getJobs()
        }, [])

        const handleDelete = (id) => {
            console.log(id);
        
            Swal.fire({
                title: 'Está seguro de ELIMINAR?',
                showDenyButton: true,
                confirmButtonText: 'Eliminar',
                denyButtonText: `No Eliminar`,
            }).then((result) => {
                /* Read more about isConfirmed, isDenied below */
                if (result.isConfirmed) {
                axios.delete(`${URL}${id}`)
                Swal.fire('Eliminado', '', 'success')
                getJobs()
                } else if (result.isDenied) {
                Swal.fire('Sus datos no se eliminaron')
                }
            })}

    return (   


            <div className="container text-center m-5">
                <h2 className="m-4">Jobs</h2>
                <Table className="table m-4">
                    <thead className="m-4">
                    {/*  <th>ID</th> */}
                    <th>Id</th>
                    <th>Title</th>
                    <th>Description</th>
                    </thead>
                    <tbody className="m-4">
                    {jobs.map(job => 
                        <tr>
                        <td>{job.id}</td>
                        <td>{job.title}</td>
                        <td>{job.description}</td>
                        <td><Button variant="success" className="mt-3">Update</Button></td>
                        <td><Button variant="danger" onClick={() => handleDelete(job._id)}className="mt-3">Delete</Button></td>
                        </tr>
                    )}
                    </tbody>
                </Table>
                </div>
    );
}
export default JobList;
