import '../css/JobList.css';
import { Table, Button } from 'react-bootstrap';
import axios from "axios";
import React, { useState, useEffect } from "react";
import Swal from 'sweetalert2';

const JobList = () => {
        

    const [jobs, setJobs] = useState([])

    const URL = 'http://localhost:3001/';

    const getJobs = async () => {
        try {
        const { data } = await axios.get(URL);
        setJobs(data.job)
        console.log(data.job);
        } catch (error) {
        console.error(error);
        }
    }
    
    useEffect( () => {
    axios.get('http://localhost:3001/').then((response) => {
        setJobs(response.data.job);
        console.log(response.data.job);
    });
}, [])


    const handleDelete = (id) => {
        console.log(id);
    
        Swal.fire({
            title: 'Are you sure you want to delete this job?',
            showDenyButton: true,
            confirmButtonText: 'Delete',
            denyButtonText: `Exit`,
            }).then((result) => {
            /* Read more about isConfirmed, isDenied below */
            if (result.isConfirmed) {
            axios.delete(`${URL}${id}`)
            Swal.fire('Deleted', '', 'success')
            getJobs()
            } else if (result.isDenied) {
            Swal.fire('This job has not been deleted')
            }
        })}

    return (   
      
            <div className="container text-center m-5">
                <h2 className="m-4">Jobs</h2>
                <div id="table">
                <table class="table">
                    <thead>
                        <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        </tr>
                    </thead>
                    <tbody>
                        
                    {jobs.map(job => 
                       <div>
                         <tr>
                            <th scope="row">1</th>
                            {/* <td>{job._id}</td> */}
                            <td>{job.title}</td>
                            </tr>   
                            <tr>
                            <th scope="row">2</th>
                            <td>{job.description}</td>
                            </tr> <tr>
                            <th scope="row">3</th>
                            <td><Button variant="success" className="mt-3">Update</Button></td>
                            <td><Button variant="danger" onClick={() => handleDelete(job._id)}className="mt-3">Delete</Button></td>
                        </tr>
                        </div>
                    )}
                    </tbody>
                </table>
                </div>
                </div>
             
    )
};

export default JobList;
