import { useContext } from 'react';
import { JobContext } from "../context/JobContext";

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Nav from 'react-bootstrap/Nav';
import './JobCard.css';

import {
    MDBCard,
    MDBCardImage,
    MDBCardBody,
    MDBCardTitle,
    MDBCardText,
    MDBCardFooter,
    MDBRow,
    MDBCol
} from 'mdb-react-ui-kit';


function JobCard({ job }) {

    const { deleteJob } = useContext(JobContext);

    return(
        <div>
            <MDBCard className='h-100'>
                    <Card.Header>
                        <Nav variant="pills" defaultActiveKey="#first">
                            <Nav.Item>
                            <Nav.Link href="#first">About Job</Nav.Link>
                            </Nav.Item>
                            <Nav.Item>
                            <Nav.Link href="#link">Job Poster</Nav.Link>
                            </Nav.Item>
                            <Nav.Item>
                            <Nav.Link href="#disabled" disabled>
                                Easy Apply
                            </Nav.Link>
                            </Nav.Item>
                        </Nav>
                    </Card.Header>
                    <MDBCardBody>
                        <MDBCardTitle>
                            {job.title}
                        </MDBCardTitle>
                        <MDBCardText>{job.description}
                        </MDBCardText>
                        <Button>Apply</Button>
                        <Button variant="danger m-1" onClick={() => deleteJob(job.id)}>Delete</Button>
                    </MDBCardBody>
                    <MDBCardFooter>
                        <small className='text-muted'>Last updated 3 mins ago
                        </small>
                    </MDBCardFooter>
            </MDBCard>
    </div>
    );
}

export default JobCard;
