//import JobCard from "./JobCard";
import { useContext, useEffect, useState } from 'react';
import { JobContext } from '../context/JobContext';
import '../css/JobList.css';
// import JobCard from './JobCard';
import axios from 'axios';


function JobList() {
    
    const [job, setJobs] = useState([]);

    const URL = 'http://localhost:3001/';

    useEffect(() => {
        const getJobs = async () => {
        try {
            const { data } = await axios.get(URL);
            setJobs(data.job)
            console.log(data.job);
        } catch (error) {
            console.error(error);
        }
        }
        getJobs()
    }, [])

    const { jobs }= useContext(JobContext);

    if (jobs.length === 0) {
        return <h1>No jobs posted yet</h1>;
    }

    return (
        <div className="JobList container album py-5 bg-ligh">
                {/* {Array.from({ length: 1 }).map((_, idx) => (
                    <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">
                        {jobs.map((job) => (
                        <JobCard key={job.id} job={job} />
                        ))}
                    </div>
                ))} */}
                <div className="container text-center m-5">
                <h2 className="m-4">Jobs</h2>
                <table className="table m-4">
                    <thead className="m-4">
                    <th>Title</th>
                    <th>Description</th>
                    </thead>
                    <body className="m-4">
                    {jobs.map(job => 
                        <tr>
                        <td>{job.title}</td>
                        <td>{job.description}</td>
                        <td><button variant="success" className="mt-3">Update</button></td>
                        <td><button variant="danger" className="mt-3">Delete</button></td>
                        </tr>
                    )}
                    </body>
                </table>
                </div>
        </div>
    );
}

export default JobList;
