import React, { useState } from 'react';
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios";
import "../css/JobForm.css";
import Swal from 'sweetalert2';

const Contact = () => {
    
        const URL = 'http://localhost:3001/contact';

        const [inputs, setInputs] = useState({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            message:""
        });

        const handleChange = (e) => {
            setInputs({
            ...inputs,
            [e.target.name]: e.target.value,
            });
        };

        const handleClick = async () => {
            await axios.post(URL, inputs)
            setInputs({
            // id: "",
            firstName: "",
            lastName: "",
            email: "",
            message:""
            })

        Swal.fire({
            title: 'Thank you!',
            text: 'Your message has been successfully sent. We will contact you very soon!',
            icon: 'success',
            confirmButtonText: 'Accept'
        })

    };

return (
    <div>
        <div className="row">
        <div className="col">
           <p>hola</p>
        </div>
        <div className="col">
        <Form className="container mt-5" id="form">
            <div className="d-flex flex-column align-items-center">
            {Object.keys(inputs).map((key, index) => (
                <Form.Group className="pt-3" key={index} style={{ maxWidth: "200px" }}>
                <Form.Label id="label">
                    {key === "" ? key.toUpperCase() : key[0].toUpperCase() + key.slice(1)}</Form.Label>
                <Form.Control
                    id="control"
                    name={key}
                    value={inputs[key]}
                    onChange={handleChange}
                />
                </Form.Group>
            ))}
            
                <Button className="mt-3" onClick={handleClick}>Send</Button>
            </div>
        </Form>
        </div>
    </div>
    </div>
  )
};

export default Contact
