import { useContext } from 'react';
import { JobContext } from "../context/JobContext";

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Nav from 'react-bootstrap/Nav';
import './JobCard.css';

function JobCard({ job }) {

    const { deleteJob } = useContext(JobContext);

    return(
        <Card className="JobCard">
            <Card.Header className="JobCardHeader">
                <Nav variant="secpnmdary" defaultActiveKey="#first">
                    <Nav.Item>
                    <Nav.Link href="#first">About Job</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                    <Nav.Link href="#link">Job Poster</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                    <Nav.Link href="#disabled" disabled>
                        Easy Apply
                    </Nav.Link>
                    </Nav.Item>
                </Nav>
            </Card.Header>
            <Card.Body className="JobCardBody">
                <Card.Title>{job.title}</Card.Title>
                <Card.Text>{job.description}</Card.Text>
                <Button id="bg-button">Apply</Button>
                <Button variant="dark m-1" onClick={() => deleteJob(job.id)}>Delete</Button>
            </Card.Body>
        </Card>
    );
}

export default JobCard;
